
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int j = 2;
		
		switch(j){
			
			case 0:
				System.out.println("Value is 0");
				break;
				
			case 2:
				System.out.println("Value is 2");
				break;
			
			case 4:
				System.out.println("Value is 4");
				break;
				
			default:
				System.out.println("No value");
				break;
		
		}

	}

}
